$(function(){var t=$("iframe"),i=$("article");t.each(function(){$(this).data("aspectRatio",this.height/this.width).removeAttr("height").removeAttr("width")}),$(window).resize(function(){var e=i.width();t.each(function(){var t=$(this);t.width(e).height(e*t.data("aspectRatio"))})}).resize()}),$(document).ready(function(){$(".sliding-panel-button,.sliding-panel-fade-screen,.sliding-panel-close").on("click touchstart",function(t){$(".sliding-panel-content,.sliding-panel-fade-screen").toggleClass("is-visible"),t.preventDefault()})});