function activateResponsiveVideos(){var e=[].slice.call(document.querySelectorAll("iframe")),t=document.querySelector("article");e.forEach(function(e){e.dataset.aspectRatio=e.height/e.width,e.removeAttribute("height"),e.removeAttribute("width")}),window.addEventListener("resize",function(){var i=window.getComputedStyle(t),n=t.clientWidth-(parseFloat(i.paddingLeft)+parseFloat(i.paddingRight));e.forEach(function(e){e.width=n,e.height=n*e.dataset.aspectRatio})}),window.dispatchEvent(new Event("resize"))}function toggleMenu(e){document.querySelector(".sliding-panel-content").classList.toggle("is-visible"),document.querySelector(".sliding-panel-fade-screen").classList.toggle("is-visible"),e.preventDefault()}function initSlidingMenu(){var e=[].slice.call(document.querySelectorAll(".sliding-panel-button, .sliding-panel-fade-screen, .sliding-panel-close"));e.forEach(function(e){e.addEventListener("click",toggleMenu),e.addEventListener("touchstart",toggleMenu)})}document.addEventListener("DOMContentLoaded",function(){initSlidingMenu(),activateResponsiveVideos()});